#include "mem_kv/mem_kv.h"
#include <cstdio>
#include <cstring>
#include <cstdlib>

int main(int argc, char *argv[])
{
    using namespace std;
    using namespace poseidon::mem_sync;

    char key[4096], val[4096], *vptr;
    size_t val_size;
    int r = 0, n = 10;
    MemKV *kvtb = new MemKV(1 <<20);

    fprintf(stdout, "Press to put, ts=%zd\r\n", time(NULL));
    for (int i = n - 1; i >= 0; --i) {
        sprintf(key, "%d:kkkkkkkkkkkkkkk", i);

        sprintf(val, "%d:", i);

        kvtb->put(strlen(key), key, strlen(val), val);
        kvtb->put(strlen(key), key, 8, "AAAAAAAF");
//        kvtb->put(sizeof(i), (char *)&i, strlen(val), val);
    }

    size_t sz = kvtb->calc_seri_size();
    char *buf = (char *)malloc(sz);

    fprintf(stdout, "seri_sz=%zd, Press to serialize, ts=%zd\r\n", sz, time(NULL));
    getchar();
    kvtb->serialize(buf, sz);

    fprintf(stdout, "Press to do get, ts=%zd\r\n", time(NULL));
    getchar();

    for (int i = 0; i < n; ++i) {
        sprintf(key, "%d:kkkkkkkkkkkkkkk", i);

//        r = kvtb->get(sizeof(i), (const char *)&i, val_size, vptr, buf, sz);
//        if (r != 0) {
//            fprintf(stderr, "r=%d key=%s\r\n", r, key);
//        }

        r = kvtb->get(strlen(key), (const char *)key, val_size, vptr, buf, sz);
        if (r != 0) {
            fprintf(stderr, "r=%d key=%s\r\n", r, key);
        }
    }

    fprintf(stdout, "Press to do iteration, ts=%zd\r\n", time(NULL));
    getchar();
    size_t ks, vs;
    char *k, *v;
    MemKVIter *iter = kvtb->get_iter(buf, sz);
    while ((r = iter->next(ks, k, vs, v)) == 0) {
        fprintf(stderr, "ks=%zd, k=%.*s, vs=%zd, val=%.*s\r\n", ks, (int)ks, k, vs, (int)vs, v);
    }

    delete iter;

    fprintf(stdout, "Press to release kvtb, ts=%zd\r\n", time(NULL));
    getchar();
    delete kvtb;

    fprintf(stdout, "Press to release seri buf\r\n");
    getchar();
    free(buf);

    fprintf(stdout, "Press to exit\r\n");
    getchar();

    return r;
}

 
